import { useState } from "react";
import { Link } from "react-router-dom"

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (event) => {
    event.preventDefault();

    const cap = localStorage.getItem('_grecaptcha');
    if (cap) {
      axios.post('http://127.0.0.1:8000/api/login',  postData, config )
      .then(response => {
        console.log(response.data.data[0]);
        localStorage.setItem('akun', JSON.stringify(response.data.data[0]));
        localStorage.setItem('isLoggedIn', 'true');

        if (response.data.data[0].MerupakanAdmin === "1") {

          localStorage.setItem('isAdmin', 'true');
          navigate("/adminpage");
        }else{
          if (isPemesanan) {
            navigate("/pemesanan");
            localStorage.removeItem('isPemesanan')
          }else{
            navigate("/userpage");
          }
        }
      })
      .catch(error => {
        console.error('Login gagal', error);
      });
    }else{
      alert("Pencet Kotak woy!!!");
    }
    
  };

  return (
    <div className='hold-transition register-page'>
      <div className="login-box">
        <div className="login-logo">
          <b>TPQ</b> Duafa
        </div>

        <div className="card">
          <div className="card-body login-card-body">
            <p className="login-box-msg">Masuk akun Anda</p>
            <img alt="" className='gambar-logo mb-3' />
            <form >
              <div className="input-group mb-3">
                <input type="text" className="form-control" placeholder="Username"/>
                <div className="input-group-append">
                  <div className="input-group-text">
                  </div>
                </div>
              </div>
              <div className="input-group mb-3">
                <input type="password" className="form-control" placeholder="Password" />
                <div className="input-group-append">
                  <div className="input-group-text">
                  </div>
                </div>
              </div>

                <div className="justify-content-center">
                  <Link to={'/userpage'}>
                    <button type="submit" className="btn btn-primary btn-block text-center">
                      Login</button>
                  </Link>
                </div>
            </form>
            <p className="my-2 text-center">
              <Link to={'/register'}>
              <a >Daftar akun baru</a>
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Login
