import axios from 'axios';
import moment from 'moment';
import 'moment/locale/id';
import React, { useEffect, useState } from 'react'

const TcrMakePresence = () => {
  const [mapel, setMapel] = useState();
  const [dariJam, setdariJam] = useState();
  const [guru, setGuru] = useState();

  const now = moment();
  now.locale('id');
  console.log(now.locale('id'))
  const formattedDate = now.format('dddd, DD MMMM YYYY - HH:mm:ss');

  const akun = JSON.parse(localStorage.getItem('akun'));
  const idCategory = akun.id_categori;

  const config = {
    headers: {
      'Content-Type': 'multipart/form-data;',
    },
  };

  const category = {
    id : idCategory
  };

  
  const showCategory = async () => {
    axios.post('http://127.0.0.1:8000/api/getCategory',  category, config )
      .then(response => {
        setMapel(response.data.data.name);
      })
      .catch(error => {
        console.error('Category not found', error);
      });
  };

  useEffect(() => {
    showCategory();
  }, []);

  return (
    <div class="content-wrapper">
    <section class="content-header">
      <div class="container-fluid">
        <div class="row mb-2">
          <div class="col-sm-6">
            <h1 className='m-0'>Buat Presensi</h1>
          </div>
          <div class="col-sm-6">
            <ol class="breadcrumb float-sm-right">
              <li class="breadcrumb-item">Beranda</li>
            </ol>
          </div>
        </div>
      </div>
    </section>

    <section class="content">
      <div class="container-fluid">
        <div class="row">
          <div class="col-md-4">

      <div className="card card-primary card-outline ">
        <div className="card-body box-profile">

          <h2 className=" mb-4">{mapel}</h2>

          <ul className="list-group list-group-unbordered mb-4">
            <li className="list-group-item">
              <i>Nama Pengajar</i> <p className="float-right">{akun.name}</p>
            </li>
            <li className="list-group-item">
              <i>Jadwal</i> <p className="float-right">Senin (Jam 17.00 - 18.00)</p>
            </li>
            <li className="list-group-item">
              <i>Tanggal</i> <p className="float-right">{formattedDate}</p>
            </li>
          </ul>

          <a href="#" className="btn btn-success btn-block"><b>Kirim Presensi</b></a>
          <a href="#" className="btn btn-outline-danger btn-block"><b>batalkan</b></a>
        </div>
      </div>
    </div>
    </div>
    </div>
    </section>
    </div>
  )
}

export default TcrMakePresence
