import axios from 'axios';
import React from 'react'

const UsrPresence = () => {
  const [dataPresen, setDataPresen] = use
  const status = {
    status : 0
  };

  
  const showCategory = async () => {
    axios.post('http://127.0.0.1:8000/api/showPresensiUsr',  status )
      .then(response => {
        setMapel(response.data.data.name);
        setDariJam(response.data.data.fromHour);
        setSampaiJam(response.data.data.untilHour);
      })
      .catch(error => {
        console.error('Category not found', error);
      });
  };


  return (
    <div class="content-wrapper">
    <section class="content-header">
      <div class="container-fluid">
        <div class="row mb-2">
          <div class="col-sm-6">
            <h1 className='m-0'>Presentasi</h1>
          </div>
          <div class="col-sm-6">
            <ol class="breadcrumb float-sm-right">
              <li class="breadcrumb-item"><a href="#">Beranda</a></li>
            </ol>
          </div>
        </div>
      </div>
    </section>

    <section class="content">
      <div class="container-fluid">
        <div class="row">
          <div class="col-md-4">

      <div className="card card-primary card-outline ">
        <div className="card-body box-profile">

          <h2 className=" mb-4">Tarjim Terjemah Al-Quran</h2>

          <ul className="list-group list-group-unbordered mb-4">
            <li className="list-group-item">
              <i>Nama Pengajar</i> <p className="float-right">Sutarno</p>
            </li>
            <li className="list-group-item">
              <i>Jadwal</i> <p className="float-right">Senin (Jam 17.00 - 18.00)</p>
            </li>
            <li className="list-group-item">
              <i>Tanggal</i> <p className="float-right">Senin, 27 Mei 2024 - 17:13:28</p>
            </li>
          </ul>

          <a href="#" className="btn btn-success btn-block"><b>Presensi</b></a>
        </div>
      </div>
    </div>
    </div>
    </div>
    </section>
    </div>
  )
}

export default UsrPresence
